import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { FaUser } from 'react-icons/fa'; // Import Font Awesome user icon
import styles from '../styles/Navbar.module.css';

const Navbar = () => {
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <nav className={`${styles.navbar} ${isScrolled ? styles.scrolled : ''}`}>
            <div className={styles.logo}>
                <Link to="/">TravelNow</Link>
            </div>
            <button
                className={styles.hamburger}
                onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
                <span className={styles.hamburgerLine}></span>
                <span className={styles.hamburgerLine}></span>
                <span className={styles.hamburgerLine}></span>
            </button>
            <ul
                className={`${styles.navLinks} ${
                    isMenuOpen ? styles.mobile : ''
                }`}
            >
                <li><Link to="/destinations">Destinations</Link></li>
                <li><Link to="/tours">Tours</Link></li>
                <li><Link to="/about-us">About Us</Link></li>
                <li><Link to="/contact">Contact</Link></li>
                <li><Link to="/booknow"><button className={styles.ctaButton}>Book Now</button></Link></li>
                <li>
                    <Link to="/loginsignup">
                        <FaUser className={styles.loginIcon} /> {/* Login Icon */}
                    </Link>
                </li>
            </ul>
        </nav>
    );
};

export default Navbar;
